import React from 'react';
import {View, StyleSheet,FlatList} from 'react-native';
import PostCard from '../components/postCard';

export default class post extends React.Component {
    constructor(props){
        super(props);
        this.state={
            data:[
                {
                    id:0,
                    title:"test",
                    description:"test",
                    author:"test",
                    date:"test",
                    view:"test",
                    tag:"test"
                },
                {
                    id:1,
                    title:"test1",
                    description:"test1",
                    author:"test1",
                    date:"test1",
                    view:"test1",
                    tag:"test1"
                },
                {
                    id:2,
                    title:"test2",
                    description:"test2",
                    author:"test2",
                    date:"test2",
                    view:"test2",
                    tag:"test2"
                },
                {
                    id:3,
                    title:"test3",
                    description:"test3",
                    author:"test3",
                    date:"test3",
                    view:"test3",
                    tag:"test3"
                },
                {
                    id:4,
                    title:"test4",
                    description:"test4",
                    author:"test4",
                    date:"test4",
                    view:"test4",
                    tag:"test4"
                }
            ]
        }
    }
    render() {
        const {data} = this.state;
        return (
            <View style={styles.container}>
                <FlatList data={data}
                    renderItem={({item})=><PostCard data={item}/>}
                    
                >
                </FlatList>
            </View>
        )
    }
}

const styles = StyleSheet.create({
    container:{
        flex:1,
        alignItems:"stretch",
        backgroundColor:"#FFF",
        justifyContent:"center",
        paddingLeft:16,
        paddingRight:16
    }
})
