import React from 'react';
import {FlatList} from 'react-native';
import CategoryCard from '../components/categoryCard';

const string = ""
export default class categories extends React.Component {
    constructor(props){
        super(props);
        this.state={
            data:[
                {
                    categorySource:"../assets/8051.jpg",
                    categoryTitle:"8051"
                },
                {
                    categorySource:"../assets/altium.jpg",
                    categoryTitle:"Altium"
                },
                {
                    categorySource:"../assets/arduino.png",
                    categoryTitle:"Arduino"
                },
                {
                    categorySource:"../assets/c-basic.png",
                    categoryTitle:"C-Basic"
                },
                {
                    categorySource:"../assets/proteus.jpg",
                    categoryTitle:"Proteus"
                },
                {
                    categorySource:"../assets/stm32.jpg",
                    categoryTitle:"STM32"
                }
            ]
        }
    }
    render() {
        const {data} = this.state;
        return (
            <FlatList data={data}
                renderItem={({item})=><CategoryCard data={item} onPress={()=>this.props.navigation.navigate("Posts")}/>}
                keyExtractor={item=>`${item.id}`}
                contentContainerStyle={{paddingLeft:5,paddingRight:5}}
            >
            </FlatList>
        )
    }
}
