import React from "react";
import Btn from "../components/btn";
import {Image,Text,View,StyleSheet} from "react-native";
import {LinearGradient} from 'expo-linear-gradient';

export default function information(){
    return(
        // <View>
        //     <LinearGradient
        //     // Background Linear Gradient
        //     colors={['#2974FA','#38ABFD','#43D4FF','transparent']}
        //     style={styles.linearGradientBg}
        //     />
        //     <View style={styles.logoutBtn}>
        //         <Btn name="Logout"/>
        //     </View>
        //     <Text style={{flex:1}}>ĐỘI NGŨ GIẢNG VIÊN</Text>
            
        // </View>
        <View style={styles.container}>
      <LinearGradient
        // Background Linear Gradient
        colors={['#43D4FF', '#38ABFD', '#2974FA','transparent']}
        style={styles.linearGradientBg}
      />
      <View style={styles.header}>
        <Image style={styles.logo} source={require("../assets/logokien-signup.8641c4b2.png")}/>
        <Text style={styles.title}>Devzone</Text>
      </View>
      <View style={styles.userInput}>
        <View style={styles.inputBox}>
          <Text style={styles.inputText}>Username:</Text>
          <TextInput style={styles.input} onChangeText={(username) => setUsername(username)} placeholder="Insert Username"/>
        </View>
        <View style={styles.inputBox}>
          <Text style={styles.inputText}>Password:</Text>
          <TextInput autoCompleteType="password" style={styles.input} onChangeText={(password) => setPassword(password)} placeholder="Insert Password"/>
        </View>
      </View>
      <View style={styles.buttonSubmit}>
        <Btn name="Login"/>
        <Btn name="Register" onPress={()=>navigation.navigate('Register')}/>
      </View>
    </View>
    );
}

const styles = StyleSheet.create({
    linearGradientBg:{
      position: 'absolute',
      left: 0,
      right: 0,
      top: 0,
      height: 500
    },
    logoutBtn:{
        alignSelf:"center",
        marginTop:10,
        marginBottom:20
    }
});
