import React from 'react';
import {FlatList} from 'react-native';
import CategoryCard from '../components/categoryCard';

export default class categories extends React.Component {
    constructor(props){
        super(props);
        this.state={
            data:[
                {
                    categorySource:"../assets/8051.jpg",
                    categoryTitle:"8051"
                },
                {
                    categorySource:"../assets/altium.jpg",
                    categoryTitle:"altium"
                },
                {
                    categorySource:"../assets/arduino.jpg",
                    categoryTitle:"8051"
                },
                {
                    id:3,
                    title:"test3",
                    description:"test3",
                    author:"test3",
                    date:"test3",
                    view:"test3",
                    tag:"test3"
                },
                {
                    id:4,
                    title:"test4",
                    description:"test4",
                    author:"test4",
                    date:"test4",
                    view:"test4",
                    tag:"test4"
                },
                {
                    id:5,
                    title:"test5",
                    description:"test5",
                    author:"test5",
                    date:"test5",
                    view:"test5",
                    tag:"test5"
                }
            ]
        }
    }
    render() {
        const {data} = this.state;
        return (
            <FlatList data={data}
                renderItem={({item})=><CategoryCard data={item} onPress={()=>this.props.navigation.navigate("Posts")}/>}
                keyExtractor={item=>`${item.id}`}
                contentContainerStyle={{paddingLeft:5,paddingRight:5}}
            >
            </FlatList>
        )
    }
}
