import React from "react";
import Btn from "../components/btn";
import {Image,Text,View,StyleSheet} from "react-native";
import {LinearGradient} from 'expo-linear-gradient';

export default function information(){
    return(
        <View style={styles.container}>
            <LinearGradient
            // Background Linear Gradient
            colors={['#2974FA','#38ABFD','#43D4FF','transparent']}
            style={styles.linearGradientBg}
            />
            <View style={styles.logoutBtn}>
                <Btn name="Logout"/>
            </View>
            <View style={styles.titleContainer}>
                <Text style={styles.title}>ĐỘI NGŨ GIẢNG VIÊN</Text>
            </View>
            <View style={styles.leaderContainer}>
                <Image style={styles.leaderAvatar} source={require("../assets/Nguyen-Viet-Dung.jpg")}></Image>
                <Text style={styles.leaderName}>Nguyễn Viết Dũng</Text>
                <Text style={styles.leaderInfo}>Hiện đang làm Master tại Đại học bách khoa Hà Nội. Quản lý Phòng nghiên cứu Thông tin vô tuyến Wicomlab C9-421 Viện điện tử Viễn Thông. Có nhiều kinh nghiệm trong thiết kế mạch Analog, lập trình nhúng</Text>
            </View>
            <View style={styles.leaderContainer}>
                <Image style={styles.leaderAvatar} source={require("../assets/Nguyen-Dinh-Thao.jpg")}></Image>
                <Text style={styles.leaderName}>Nguyễn Đình Thảo</Text>
                <Text style={styles.leaderInfo}>Hiện đang làm Master tại Đại học bách khoa Hà Nội tốt nghiệp chuyên ngành Điện Tử Viễn Thông. Có hơn 3 năm kinh nghiệm trong lập trình nhúng và thiết kế mạch (Hardware and Software Embedded). Hiện đang làm việc tại Tổng Công Ty CN CNC Viettel.</Text>
            </View>
            <View style={styles.leaderContainer}>
                <Image style={styles.leaderAvatar} source={require("../assets/Nguyen-Viet-Dung.jpg")}></Image>
                <Text style={styles.leaderName}>Lê Anh Tuấn Dương</Text>
                <Text style={styles.leaderInfo}>Hiện đang làm Master tại Đại học bách khoa Hà Nội. Quản lý Phòng nghiên cứu Thông tin vô tuyến Wicomlab C9-421 Viện điện tử Viễn Thông. Có nhiều kinh nghiệm trong thiết kế mạch Analog, lập trình nhúng</Text>
            </View>
            <View style={styles.leaderContainer}>
                <Image style={styles.leaderAvatar} source={require("../assets/Nguyen-Viet-Dung.jpg")}></Image>
                <Text style={styles.leaderName}>Nguyễn Viết Dũng</Text>
                <Text style={styles.leaderInfo}>Hiện đang làm Master tại Đại học bách khoa Hà Nội. Quản lý Phòng nghiên cứu Thông tin vô tuyến Wicomlab C9-421 Viện điện tử Viễn Thông. Có nhiều kinh nghiệm trong thiết kế mạch Analog, lập trình nhúng</Text>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    linearGradientBg:{
      position: 'absolute',
      left: 0,
      right: 0,
      top: 0,
      height: 500
    },
    logoutBtn:{
        alignSelf:"center",
        marginTop:10,
        marginBottom:10
    },
    container: {
      flex: 1
    },
    titleContainer:{
        alignItems:"center",
        borderBottomWidth:2,
        borderBottomColor:"#000",
        paddingBottom:5,
        marginLeft:10,
        marginRight:10,
        marginBottom:5
    },
    title:{
        fontFamily:"monospace",
        fontWeight:"bold",
        fontSize:20
    },
    leaderContainer:{
        alignItems:"center",
        borderBottomWidth:2,
        borderBottomColor:"#000",
        paddingBottom:10,
        marginLeft:10,
        marginRight:10
    },
    leaderAvatar:{
        width:100,
        height:100,
        borderRadius:50,
        margin:5
    },
    leaderName:{
        fontWeight:"bold",
        fontSize:15
    },
    leaderInfo:{

    }
});
