import { StatusBar } from 'expo-status-bar';
import React,{Component} from 'react';
import { StyleSheet, Text, View, Image, Button, TouchableOpacity, TextInput} from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';

export default function App() {
  return (
    <View style={styles.container}>
      <LinearGradient
        // Background Linear Gradient
        colors={['rgba(0,0,255,0.8)', 'transparent']}
        style={{
          position: 'absolute',
          left: 0,
          right: 0,
          top: 0,
          height: 300,
        }}
      />
      <View style={styles.header}>
        <Image style={styles.logo} source={require("./assets/logokien-signup.8641c4b2.png")}/>
        <Text style={styles.title}>Devzone</Text>
      </View>
      <View style={styles.userInput}>
        <View style={styles.inputBox}>
          <Text style={styles.inputText}>Username:</Text>
          <TextInput style={styles.input} onChangeText={text => onChangeText(text)} value="" placeholder="Insert Username"/>
        </View>
        <View style={styles.inputBox}>
          <Text style={styles.inputText}>Password:</Text>
          <TextInput style={styles.input} onChangeText={text => onChangeText(text)} value="" placeholder="Insert Password"/>
        </View>
      </View>
      <View style={styles.buttonSubmit}>
        <View style={styles.button}><Button title="Login"></Button></View>
        <View style={styles.button}><Button title="Register"></Button></View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,

  },
  header:{
    flex:1,
    display:"flex",
    flexDirection:"row",
    justifyContent:"center",
    alignItems:"center"
  },
  logo:{
    width:50,
    height:50
  },
  title:{
    marginLeft:5,
    fontSize:35,
    fontWeight:"bold",
    height:50
  },
  userInput:{
    flex:1,
    paddingLeft:70,
    paddingRight:70,
    bottom:10
  },
  inputBox:{
    display:"flex",
    flexDirection:"row",
    justifyContent:"space-between",
    alignItems:"flex-start",
    alignItems:"center",
    marginTop:5
  },
  inputText:{
    flex:1,
    fontSize:20,
    fontWeight:"bold",
    height:30,
  },
  input:{
    flex:1,
    backgroundColor:"white",
    height:27,
    borderRadius:25,
    textAlign:"center",
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 1 },
    shadowOpacity: 0.5,
    shadowRadius: 2,  
    elevation: 5
  },
  buttonSubmit:{
    flex:1,
    bottom:100
  },
  button:{
    borderRadius:25,

  }
});
