import React from 'react';
import {View,FlatList,ActivityIndicator} from 'react-native';
import PostCard from '../components/postCard';
import Btn from '../components/postCard';

const axios = require('axios');

export default class posts extends React.Component {
    constructor(props){
        super(props);
        this.state={
            data:[],
            isLoading:true
        }
    }
    componentDidMount(){
        let data=[];
        axios.get("https://devzone.vn/backend/tutorials?page=2").then(response=>{
            for (let i = 0;i<response.data.length;i++){
                data.push(response.data[i]);
            }
        }).then(()=>{
            this.setState({
                isLoading:false,
                data:data
            })
        })
    }
    viewMorePostAPI= ()=>{

    }
    render() {
        if(this.state.isLoading){
            return (
                <View>
                    <ActivityIndicator size="large" animating></ActivityIndicator>
                </View>
            )
        }
        else {
            console.log(this.state.data);
            return (
            <View style={{flex:1}}>
                <View style={{flex:1}}>
                <FlatList data={this.state.data}
                    renderItem={({item})=><PostCard data={item} onPress={()=>this.props.navigation.navigate("Post")}/>}
                    keyExtractor={item=>`${item.id}`}
                    contentContainerStyle={{paddingLeft:10,paddingRight:10}}
                >
                </FlatList>
                </View>
                <Btn name = "View more" onPress={this.viewMorePostAPI}/>
            </View>
        )}
    }
}
